import React, { useEffect } from "react";
// Redux
import { connect } from "react-redux";
import { NotificationManager } from "react-notifications";
import LinearProgress from "@material-ui/core/LinearProgress";
import { Formik, Field, Form } from "formik";
import * as Yup from "yup";
import { labels } from "../../constants/index";
// Actions
import { signUp, clean } from "../../store/actions";
import { hideModal } from "../../store/actions/ModalActions";
import Input from "../Forms/Input.js";
import Message from "../Message";

import Modal from "../Modal";

const LoginSchema = Yup.object().shape({
  email: Yup.string()
    .email(`email dosen't work :/`)
    .required("You need an email silly!"),
  password: Yup.string().required("You need a password silly!")
});

const Login = () => {
  return (
    <Modal title="Sign In">
      <Form className="signUpWrapper">
        <Field
          type="text"
          label={labels.username}
          name="userName"
          component={Input}
        />
        <Field
          type="email"
          label={labels.email}
          name="email"
          component={Input}
        />
        <Field
          type="password"
          label={labels.password}
          name="password"
          component={Input}
        />
        <Field
          type="password"
          label={labels.confirmPassword}
          name="confirmPassword"
          component={Input}
        />
        <div className="authCheckbox">
          <Field
            type="checkbox"
            label={labels.terms}
            name="acceptedTerms"
            component={Input}
          />
        </div>

        <button disabled={!isValid || isSubmitting} type="submit">
          Create My Account
        </button>
        {loading && (
          <div style={{ textAlign: "center" }}>
            <LinearProgress />
          </div>
        )}
        <div>
          <Message error show={error}>
            {NotificationManager.error({ error })}
          </Message>
        </div>
      </Form>
    </Modal>
  );
};

export default Login;
